document.addEventListener("DOMContentLoaded",function(){const e=new IntersectionObserver(t=>{t.forEach(t=>{if(t.isIntersecting){const a=t.target;if(a.hasAttribute("data-animate-stagger")){a.querySelectorAll("[data-animate]").forEach((e,t)=>{e.style.transitionDelay=100*t+"ms",e.classList.add("is-in-view")})}else a.classList.add("is-in-view");e.unobserve(a)}})},{threshold:.1});document.querySelectorAll("[data-animate], [data-animate-stagger]").forEach(t=>{e.observe(t)}),document.querySelectorAll(".has-dropdown > .dropdown-toggle").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=this.closest(".has-dropdown");t.classList.toggle("active"),document.querySelectorAll(".has-dropdown").forEach(e=>{e!==t&&e.classList.remove("active")})})}),document.addEventListener("click",function(e){e.target.closest(".has-dropdown")||document.querySelectorAll(".has-dropdown").forEach(e=>e.classList.remove("active"))})});